import React, { useState, useEffect, useRef } from 'react';
import GoogleMapReact from 'google-map-react';
import axios from 'axios';

const HospitalMarker = ({ text }) => <div style={{ color: 'red' }}>{text}</div>;

export const MapsPage = () => {
  const [userLocation, setUserLocation] = useState(null); // Start with null to indicate no location yet
  const [userLocationFetched, setUserLocationFetched] = useState(false); // Track whether user location has been fetched
  const [hospitals, setHospitals] = useState([]);
  const mapRef = useRef(null);
  const mapsRef = useRef(null);

  // Simulate fetching hospitals
  const fetchHospitals = (location) => {
    const { lat, lng } = location;
    // Replace with your actual fetch logic
    axios.get(`http://localhost:8081/api/hospitals?lat=${lat}&lng=${lng}`)
      .then(response => {
        setHospitals(response.data.results);
      })
      .catch(error => {
        console.error('Error fetching hospitals:', error);
      });
  };

  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition((position) => {
        const pos = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        };
        setUserLocation(pos);
        setUserLocationFetched(true); // Mark user location as fetched
        fetchHospitals(pos);

        // Update map center
        if (mapRef.current) {
          mapRef.current.panTo(pos);
        }
      });
    }
  }, []);

  useEffect(() => {
    if (mapRef.current && mapsRef.current && hospitals.length > 0) {
      hospitals.forEach((hospital) => {
        new mapsRef.current.Marker({
          position: {
            lat: hospital.geometry.location.lat,
            lng: hospital.geometry.location.lng
          },
          map: mapRef.current,
          title: hospital.name,
        });
      });
    }
  }, [hospitals]);

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <GoogleMapReact
        bootstrapURLKeys={{ key: process.env.REACT_APP_GOOGLE_MAPS_API_KEY }}
        defaultCenter={userLocation || { lat: 59.95, lng: 30.33 }} // Fallback to a default center if userLocation is null
        defaultZoom={14}
        yesIWantToUseGoogleMapApiInternals
        onGoogleApiLoaded={({ map, maps }) => {
          mapRef.current = map;
          mapsRef.current = maps;
          if (userLocationFetched && userLocation) {
            new maps.Marker({
              position: userLocation,
              map: mapRef.current,
              title: 'Your Location',
            });
            map.panTo(userLocation);
          }
        }}
      >
        {hospitals.map((hospital, index) => (
          <HospitalMarker
            key={index}
            lat={hospital.geometry.location.lat}
            lng={hospital.geometry.location.lng}
            text={hospital.name}
          />
        ))}
      </GoogleMapReact>
    </div>
  );
};
